mapper.select:
SELECT 

FROM oa_dossier a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM oa_dossier a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE oa_dossier SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
